// Scroll reveal and back to top functionality
document.addEventListener('DOMContentLoaded',function(){const e=document.querySelectorAll('.reveal'),t=new IntersectionObserver(e=>{e.forEach(e=>{e.isIntersecting&&(e.target.classList.add('visible'),t.unobserve(e.target))})},{threshold:.1,rootMargin:'0px 0px -40px 0px'});e.forEach(e=>t.observe(e));const n=document.getElementById('backToTop');window.addEventListener('scroll',()=>{n.style.display=window.scrollY>400?'flex':'none',n.style.alignItems='center',n.style.justifyContent='center'}),n.addEventListener('click',()=>window.scrollTo({top:0,behavior:'smooth'}))});
